// SPDX-License-Identifier: MIT
pragma solidity ^0.8.26;

/**
 * @title IBridgeVerifier
 * @notice Interface for cross-chain message and proof verification in Cipher Zero Protocol
 * @dev Defines the core functionality for verifying cross-chain messages and their associated ZK proofs
 */
interface IBridgeVerifier {
    /**
     * @notice Message verification status
     * @param UNVERIFIED Initial state
     * @param PENDING Verification in progress
     * @param VERIFIED Successfully verified
     * @param FAILED Verification failed
     * @param EXPIRED Message timeout
     */
    enum MessageStatus {
        UNVERIFIED,
        PENDING,
        VERIFIED,
        FAILED,
        EXPIRED
    }

    /**
     * @notice Cross-chain message structure
     * @param messageHash Hash of message data
     * @param sourceChain Source chain ID
     * @param targetChain Target chain ID
     * @param payloadHash Hash of payload data
     * @param nullifier Unique nullifier
     * @param sender Original sender
     * @param recipient Target recipient
     * @param status Message status
     * @param timestamp Message timestamp
     * @param proof ZK proof hash
     */
    struct CrossChainMessage {
        bytes32 messageHash;
        uint16 sourceChain;
        uint16 targetChain;
        bytes32 payloadHash;
        bytes32 nullifier;
        address sender;
        address recipient;
        MessageStatus status;
        uint256 timestamp;
        bytes32 proof;
    }

    /**
     * @notice Guardian action structure
     * @param guardian Guardian address
     * @param messageHash Message hash
     * @param signature Guardian signature
     * @param timestamp Action timestamp
     */
    struct GuardianAction {
        address guardian;
        bytes32 messageHash;
        bytes signature;
        uint256 timestamp;
    }

    /**
     * @notice Emitted when a cross-chain message is verified
     * @param messageHash Hash of the verified message
     * @param sourceChain Source chain ID
     * @param targetChain Target chain ID
     * @param sender Message sender
     * @param recipient Message recipient
     * @param timestamp Verification timestamp
     */
    event MessageVerified(
        bytes32 indexed messageHash,
        uint16 indexed sourceChain,
        uint16 indexed targetChain,
        address sender,
        address recipient,
        uint256 timestamp
    );

    /**
     * @notice Emitted when a cross-chain message fails verification
     * @param messageHash Hash of the failed message
     * @param reason Failure reason
     * @param timestamp Failure timestamp
     */
    event MessageVerificationFailed(
        bytes32 indexed messageHash,
        string reason,
        uint256 timestamp
    );

    /**
     * @notice Emitted when a guardian set is updated
     * @param index Guardian set index
     * @param guardians New guardian addresses
     * @param timestamp Update timestamp
     */
    event GuardianSetUpdated(
        uint32 indexed index,
        address[] guardians,
        uint256 timestamp
    );

    /**
     * @notice Emitted when a proof is verified
     * @param messageHash Associated message hash
     * @param proofHash Hash of the verified proof
     * @param timestamp Verification timestamp
     */
    event ProofVerified(
        bytes32 indexed messageHash,
        bytes32 indexed proofHash,
        uint256 timestamp
    );

    /**
     * @notice Verify a cross-chain message with ZK proof
     * @param encodedVM Wormhole encoded verified message
     * @param proof ZK proof data
     * @return success Verification success status
     */
    function verifyMessage(
        bytes memory encodedVM,
        bytes memory proof
    ) external returns (bool success);

    /**
     * @notice Verify a guardian signature
     * @param messageHash Message hash
     * @param signature Guardian signature
     * @param guardian Guardian address
     * @return valid Signature validity
     */
    function verifyGuardianSignature(
        bytes32 messageHash,
        bytes memory signature,
        address guardian
    ) external view returns (bool valid);

    /**
     * @notice Check if a message is verified
     * @param messageHash Message hash to check
     * @return verified Verification status
     */
    function isMessageVerified(
        bytes32 messageHash
    ) external view returns (bool verified);

    /**
     * @notice Get message status
     * @param messageHash Message hash to check
     * @return status Current message status
     */
    function getMessageStatus(
        bytes32 messageHash
    ) external view returns (MessageStatus status);

    /**
     * @notice Get message details
     * @param messageHash Message hash to query
     * @return message CrossChainMessage struct
     */
    function getMessage(
        bytes32 messageHash
    ) external view returns (CrossChainMessage memory message);

    /**
     * @notice Get active guardian set
     * @return guardians Array of current guardian addresses
     */
    function getGuardians() external view returns (address[] memory guardians);

    /**
     * @notice Check if an address is a guardian
     * @param guardian Address to check
     * @return isGuardian Whether address is a current guardian
     */
    function isGuardian(
        address guardian
    ) external view returns (bool isGuardian);

    /**
     * @notice Get required number of guardian signatures
     * @return required Number of required signatures
     */
    function getRequiredSignatures() external view returns (uint256 required);

    /**
     * @notice Get supported source chains
     * @return sourceChains Array of supported source chain IDs
     */
    function getSupportedSourceChains() external view returns (uint16[] memory sourceChains);

    /**
     * @notice Check if source chain is supported
     * @param chainId Chain ID to check
     * @return supported Whether chain is supported
     */
    function isSourceChainSupported(
        uint16 chainId
    ) external view returns (bool supported);

    /**
     * @notice Get message timeout period
     * @return timeout Timeout period in seconds
     */
    function getMessageTimeout() external view returns (uint256 timeout);

    /**
     * @notice Check if message has timed out
     * @param messageHash Message hash to check
     * @return timedOut Whether message has timed out
     */
    function isMessageTimedOut(
        bytes32 messageHash
    ) external view returns (bool timedOut);

    /**
     * @notice Check if nullifier has been used
     * @param nullifier Nullifier to check
     * @return used Whether nullifier has been used
     */
    function isNullifierUsed(
        bytes32 nullifier
    ) external view returns (bool used);

    /**
     * @notice Get guardian actions for a message
     * @param messageHash Message hash to query
     * @return actions Array of guardian actions
     */
    function getGuardianActions(
        bytes32 messageHash
    ) external view returns (GuardianAction[] memory actions);
}

